using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using AtidRegister.Areas.Admin.Models;
using AtidRegister.Configuration;
using AtidRegister.Models;
using AtidRegister.Models.ViewModels;
using AtidRegister.Services;
using AtidRegister.Services.Conference;
using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;
using Microsoft.Extensions.Options;

namespace AtidRegister.Areas.Admin.Controllers
{
    [Area("Admin")]
    public class ContentsController : Controller
    {
        #region constants
        private const string defaultBase64Banner = "image/jpg;base64,";
        #endregion

        #region Services
        private readonly IContentsService _contentsService;
        private readonly AppConfig _config;
        private readonly IUserService _userService;
        private readonly IPeopleService _peopleService;
        #endregion

        public ContentsController(IUserService userService, IContentsService contentsService,
            IOptions<AppConfig> config, IPeopleService peopleService)
        {
            _userService = userService;
            _contentsService = contentsService;
            _config = config.Value;
            _peopleService = peopleService;
        }

        #region List
        public async Task<IActionResult> Index() => View((await _contentsService.GetContentsAsync()).OrderBy(c => c.Type.Id));
        #endregion

        #region Details
        public async Task<IActionResult> Details(int id)=> View(await _contentsService.FindContentByIdAsync(id));
        #endregion

        #region Create
        [HttpGet]
        public async Task<IActionResult> Create()
        {
            var model = new CreateContentViewModel()
            {
                Types = await _contentsService.GetContentTypesAsync(),
                People = await GetEmptyPeopleList(),
                TimeStrips = await _contentsService.GetTimeStripsAsync()
            };
            return View(model);
        }

        private async Task<List<PersonCheck>> GetEmptyPeopleList()
        {
            var peopleList = await _peopleService.GetAsync();
            var res = new List<PersonCheck>();
            foreach (var person in peopleList)
            {
                res.Add(new PersonCheck() { isChecked = false, Person = person });
            }
            return res;
        }


        [HttpPost]
        public async Task<IActionResult> Create(CreateContentViewModel model)
        {

            if (ModelState.IsValid)
            {
                // validate that there's at least one person:
                if (!(model.People != null && model.People.Any(p => p.isChecked)))
                {
                    ModelState.AddModelError("People", "נא לבחור לפחות מרצה אחד.");
                    model.Types = await _contentsService.GetContentTypesAsync();
                    model.People = await GetEmptyPeopleList();
                    model.TimeStrips = await _contentsService.GetTimeStripsAsync();
                    return View(model);
                }
                // get type
                ContentType type = await _contentsService.FindContentTypeByIdAsync(model.TypeId);
                if (type == null) throw new InvalidOperationException("Type couldn't be found.");
                string image = defaultBase64Banner;
                if (model.Image != null)
                {
                    image = await ImageService.GetJpegBase64String(model.Image);
                }
                int id = await _contentsService.AddContentAsync(model.Title, model.Description, model.People.Where(p => p.isChecked).Select(p => p.Person).ToList(), type, image, model.TimeStripId);
                return RedirectToAction("Details", new { id });
            }
            // get data if failed
            model.Types = await _contentsService.GetContentTypesAsync();
            model.People = await GetEmptyPeopleList();
            model.TimeStrips = await _contentsService.GetTimeStripsAsync();
            return View(model);
        }
        #endregion

        

        #region Delete
        [HttpGet]
        public async Task<IActionResult> Delete(int id) => View(await _contentsService.FindContentByIdAsync(id));
        [HttpPost]
        public async Task<IActionResult> Delete(int Id, string a)
        {
            await _contentsService.RemoveContentAsync(Id);
            return RedirectToAction("Index");
        }
        #endregion

        #region Edit
        [HttpGet]
        public async Task<IActionResult> Edit(int id)
        {
            var content = await _contentsService.FindContentByIdAsync(id);
            return View(new EditContentViewModel
            {
                Title = content.Title,
                Description = content.Description,
                Id = content.Id,
                OldImageName = content.ImageFile,
                People = await GetContentPeopleChecksAsync(content),
                TimeStrips = await _contentsService.GetTimeStripsAsync(),
                TimeStripId = content.TimeStripId
            }); ;
        }

        private async Task<List<PersonCheck>> GetContentPeopleChecksAsync(Content content)
        {
            var result = new List<PersonCheck>();
            var allPeople = await _peopleService.GetAsync();
            foreach (var person in allPeople)
            {
                result.Add(new PersonCheck() { isChecked = await _peopleService.IsInContentAsync(person, content), Person = person });
            }
            return result;
        }
        [HttpPost]
        public async Task<IActionResult> Edit(EditContentViewModel model)
        {
            if (ModelState.IsValid)
            {
                bool isNewImage = model.NewImage != null;
                var content = await _contentsService.FindContentByIdAsync(model.Id);
                content.Title = model.Title;
                content.Description = model.Description;
                content.TimeStripId = model.TimeStripId;
                if (isNewImage)
                    content.ImageFile = await ImageService.GetJpegBase64String(model.NewImage);
                await _contentsService.UpdateContentAsync(content, model.People.Where(p => p.isChecked).Select(p => p.Person).ToList());
                return RedirectToAction("Details", new { id = content.Id });
            }
            model.People = await GetContentPeopleChecksAsync(await _contentsService.FindContentByIdAsync(model.Id));
            model.TimeStrips = await _contentsService.GetTimeStripsAsync();
            return View(model);
        }
        #endregion

    }
}